import { useEffect, useState } from "react"
import { fetchAllStudentWorksheets } from './services/worksheetServices'

export const StudentAnswers = ({ user }) => {

    const [studentWorksheets, setStudentWorksheets] = useState([])

    const handlestudentWorksheetFetch = () => {
        fetchAllStudentWorksheets().then((data) => {
            // this needs to be fixed
            const filteredData = data.filter((datum) => datum.studentId === user.id)
            setStudentWorksheets(filteredData)
        })
    }

    useEffect(() => {
        handlestudentWorksheetFetch()
    }, [user?.id])

    return (
        <>
            {studentWorksheets.map((studentWorksheet) => {
                const worksheetMap = studentWorksheet?.worksheet?.questionAndAnswers.map((sheet, sheetIndex) => {
                    <>
                        <p>{sheet?.question}</p>
                        <input placeholder="answer" />
                        <p>{sheet?.hint}</p>
                    </>
                })
                console.log("ðŸš€ ~ file: StudentAnswers.js:35 ~ worksheetMap ~ worksheetMap:", worksheetMap)
                html += worksheetMap?.join('')
            })}
        </>
    )
}
