export const fetchUserWorksheets = (id) => {
    return (
        fetch(`http://localhost:8088/worksheets?userId=${id}`).then((response) => response.json())
    )
}

export const postWorksheetToDatabase = (object) => {
    return (
        fetch('http://localhost:8088/worksheets', {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(object)
        })
    )
}

export const fetchStudentWorksheetById = (id) => {
    return (
        fetch(`http://localhost:8088/worksheets?id=${id}&_expand=student`).then((response) => response.json())
    )
}

