import { useEffect } from "react"
import { useState } from "react"
import { Link } from "react-router-dom"
import { fetchUserWorksheets } from "../services/worksheetServices"
import { fetchSubjects } from "../services/subjectServices"
import { fetchUserStudents } from "../services/studentServices"

export const Worksheets = ({ user }) => {

  const [students, setStudents] = useState([])
  const [subjects, setSubjects] = useState([])
  const [worksheets, setWorksheets] = useState([])
  const [worksheetsToDisplay, setworksheetsToDisplay] = useState([])

  const handleFilters = (event) => {
    const name = event.target.name
    const value = event.target.value
    const filterWorksheets = worksheets.filter((worksheet) => {
      return worksheet[name] === value
    })
    setworksheetsToDisplay(filterWorksheets)
  }

  const handleFetchCalls = () => {
    fetchUserWorksheets(user.id).then((data) => {
      setWorksheets(data)
    })
    fetchSubjects().then((data) => {
      setSubjects(data)
    })
    fetchUserStudents(user.id).then((data) => {
      setStudents(data)
    })
  }

  useEffect(() => {
    handleFetchCalls()
  }, [user])

  useEffect(() => {
    setworksheetsToDisplay(worksheets)
  }, [worksheets])

  // add a like relationship to the posts

  // teacher can specify what subject she teaches

  // a teacher should be able to upload a worksheet and assign it to a student
  // a parent can then re upload the worksheet to show that it is ready for review
  // a teacher should be able to then very that it is completed and mark it as completed

  // a post or comment page for people to communicate and leave comments on the post 

  return (
    <>

    </>
  )
}
