import { useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { createUser, getUserByEmail, fetchUserTypes } from "../services/userServices"
import { fetchStudents } from '../services/studentServices'

export const Register = (props) => {

  const object = {
    aboutMe: "Edit your profile to update your about me section!",
    email: "",
    firstName: "",
    lastName: "",
    userTypeId: ""
  }

  const [studentCode, setStudentCode] = useState(0)
  const [user, setUser] = useState(object)

  const [userTypes, setUserTypes] = useState([])

  const navigate = useNavigate()

  const registerNewUser = (copyOfUser) => {
    createUser(copyOfUser).then((createdUser) => {
      if (createdUser.hasOwnProperty("id")) {
        localStorage.setItem(
          "capstone_user",
          JSON.stringify({
            id: createdUser.id,
          }),
        )
        navigate("/")
      }
    })
  }

  const handleFetchCalls = () => {
    fetchUserTypes().then((data) => {
      setUserTypes(data)
    })
  }

  const handleStudentCode = (event) => {
    setStudentCode(event.target.value)
  }

  const handleStudentLogin = (event) => {
    event.preventDefault()
    fetchStudents().then((data) => {
      const object = data.find((dataset) => `${dataset.studentCode}` + `${dataset.id}` === studentCode)
      if (object) {
        const userToCreate = {
          aboutMe: "Edit your profile to update your about me section!",
          email: `${object?.firstName}.${object?.lastName}@student.com`,
          firstName: object?.firstName,
          lastName: object?.lastName,
          studentId: object?.id,
          userTypeId: 2
        }
        createUser(userToCreate).then(() => {
          window.alert(`${userToCreate?.email}`)
        })
      } else {
        window.alert('No student matches this code')
      }
    })
  }

  const handleRegisterUser = (e) => {
    e.preventDefault()
    const copyOfUser = { ...user, email: `${user?.firstName}.${user?.lastName}@${userTypes.find((userType) => userType?.id === user?.userTypeId * 1).description?.toLowerCase()}.com` }
    copyOfUser.userTypeId = parseInt(copyOfUser?.userTypeId)
    // setUser(copyOfUser)
    getUserByEmail(user?.email).then((response) => {
      if (response.length > 0) {
        // Duplicate email. No good.
        window.alert("Account with that email address already exists")
      } else {
        // Good email, create user.
        registerNewUser(copyOfUser)
      }
    })
  }

  const handleUserInput = (event) => {
    const copy = { ...user }
    const name = event?.target?.name
    const value = event?.target?.value
    copy[name] = value
    setUser(copy)
  }

  const handleUpdateUser = (evt) => {
    const copy = { ...user }
    copy[evt.target.id] = evt.target.value
    setUser(copy)
  }

  useEffect(() => {
    handleFetchCalls()
  }, [])

  return (
    <>
      <fieldset>
        <p
          className="block text-sm font-medium leading-6 text-gray-900">
          First Name
        </p>
        <input
          className="mt-2 text-center block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
          autoComplete="first name"
          id="firstName"
          onChange={handleUpdateUser}
          placeholder="Enter your first name"
          required
          type="text"
          value={user?.userTypeId * 1 === 2 ? "enter student code" : user?.firstName} />
      </fieldset>
      <fieldset>
        <label
          className="block text-sm font-medium leading-6 text-gray-900">
          Last Name
        </label>
        <input
          className="mt-2 text-center block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
          id="lastName"
          onChange={handleUpdateUser}
          placeholder="Enter your last name"
          required
          type="text"
          value={user?.userTypeId * 1 === 2 ? "enter student code" : user?.lastName} />
      </fieldset>
      <div className="sm:col-span-4">
        <label htmlFor="username" className="block text-sm font-medium leading-6 text-gray-900">Email</label>
        <div className="flex mt-2 rounded-md shadow-sm ring-1 ring-inset ring-gray-300 focus-within:ring-2 focus-within:ring-inset focus-within:ring-indigo-600 sm:max-w-md">
          <span
            className="text-center border-0 bg-transparent py-1.5 focus:ring-0  sm:leading-6 flex select-none pl-2 text-gray-500 sm:text-sm">
            {user?.firstName || user?.lastName ? `${user?.firstName?.toLowerCase()}.${user?.lastName?.toLowerCase()}` : null}{user?.userTypeId ? "@" + userTypes.find((userType) => userType?.id === user?.userTypeId * 1).description?.toLowerCase() + ".com" : "@email.com"}
          </span>
        </div>
      </div>
      <button
        className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
        onClick={handleRegisterUser}>
        Sign up
      </button>
      <p
        className="mt-10 text-center text-sm text-gray-500">
        Already a member?{" "}
        <Link
          to="/login"
          className="font-semibold leading-6 text-indigo-600 hover:text-indigo-500">
          Login
        </Link>
      </p>
    </>
  )
}
