import { useEffect, useState } from "react"
import { fetchGrades } from '../services/gradeServices'
import { fetchUserStudents } from '../services/studentServices'

export const Students = ({ user }) => {

    const [grades, setGrades] = useState([])
    const [userStudents, setUserStudents] = useState([])

    const handleFetchGrades = () => {
        fetchGrades().then((data) => { setGrades(data) })
    }

    const handleFetchUserStudents = () => {
        fetchUserStudents(user.id).then((data) => {
            setUserStudents(data)
        })
    }

    useEffect(() => {
        handleFetchUserStudents()
        handleFetchGrades()
    }, [user.id])

    return (
        <>
        <div></div>
            <aside className="border">
                <div className="flex">
                    <section className="flex flex-col">
                        {userStudents.map((userStudent) => {
                            return (
                                <>
                                    <aside className="flex gap-5 justify-start p-10">
                                        <div className="">
                                            <img className="bg-white hover:w-24 rounded-full shadow-lg w-20" src={`https://api.dicebear.com/7.x/big-smile/svg?seed=${userStudent.firstName}%20${userStudent.lastName}`} alt="" />
                                        </div>
                                        <div className="flex flex-col justify-around">
                                            <p>{userStudent.firstName} {userStudent.lastName}</p>
                                            <p className="text-gray-500">{grades.find((grade) => grade.id === userStudent.gradeId)?.level} Grade</p>
                                        </div>
                                    </aside>
                                </>
                            )
                        })}
                    </section>
                </div>
            </aside>
            <main className="bg-gray-500">main section here
            </main>
        </>
    )
}
