import { useEffect, useState } from "react"
import { Home } from "../home/Home"
import { Outlet, Route, Routes } from "react-router-dom"
import { fetchUserById } from "../services/userServices"
import { NavBar } from "../components/NavBar"
import { Students } from "../students/Students"

export const ApplicationViews = () => {

  const [user, setUser] = useState({})

  const handleFetchCalls = (id) => {
    fetchUserById(id).then((data) => {
      setUser(data)
    })
  }

  useEffect(() => {
    const localUser = localStorage.getItem("capstone_user")
    const userObject = JSON.parse(localUser)
    handleFetchCalls(userObject.id)
  }, [])

  return (
    <Routes>
      <Route
        path="/"
        element={
          <>
            <NavBar />
            <Outlet>

            </Outlet>
          </>
        }><Route
          index
          element={<Home user={user} />}>
        </Route>
        <Route
          path="students"
          element={<Students user={user} />}>
            <Route
            path=":studentId">

            </Route>
        </Route>
        <Route></Route>
      </Route>
    </Routes>
    // currentUser.isStaff ? <EmployeeViews currentUser={currentUser} /> : <CustomerViews currentUser={currentUser}/>
  )
}
