import { useEffect, useState } from "react"
import { deleteWorksheetFromDatabase, fetchAllStudentWorksheets, fetchUserWorksheets } from "../services/worksheetServices"
import { useNavigate } from "react-router-dom"
import { NavBar } from "../components/NavBar"

export const Worksheets = ({ user }) => {

    const [studentWorksheets, setStudentWorksheets] = useState([])
    const [worksheets, setWorksheets] = useState([])

    const navigate = useNavigate()

    const handleDeleteButton = (event, id) => {
        event.preventDefault()
        // deleteWorksheetFromDatabase(id).then(() => {
        //     handleWorksheetsFetchCall()
        // })
    }

    const handleWorksheetsFetchCall = () => {
        fetchUserWorksheets(user.id).then((data) => {
            setWorksheets(data)
        })
        fetchAllStudentWorksheets().then((data) => {
            setStudentWorksheets(data)
        })
    }
    useEffect(() => {
        handleWorksheetsFetchCall()
    }, [user.id])

    return (
        <div className="flex">
            <aside className="bg-gray-300">side bar will go here</aside>
            <div className="bg-gray-400">
                <header>
                    <h1 className="text-4xl font-medium">Worksheets</h1>
                </header>
                <main className="grid grid-cols-4">
                    {worksheets.map((worksheet) => {
                        return (
                            <section className="bg-gray-500 rounded shadow-xl p-1 m-3">
                                <img src="https://placehold.co/200" alt="" />
                                <p>{worksheet?.title}</p>
                                {worksheet?.userId === user?.id ?
                                    <div className="flex justify-end gap-5">
                                        <button className="rounded-md bg-blue-500 hover:bg-blue-400 w-16">
                                            Edit
                                        </button>
                                        <button className="rounded-md bg-red-500 hover:bg-red-400 w-16">
                                            Delete
                                        </button>
                                    </div> :
                                    <div className="flex justify-end">
                                        <b></b>
                                        <button className="border rounded-full w-10 hover:bg-yellow-400">
                                            üëç
                                        </button>
                                    </div>
                                }
                            </section>
                        )
                    })}
                </main>
            </div>
            <aside className="bg-gray-500">side bar will go here</aside>
        </div>
    )
}
