import { useEffect, useState } from "react"
import { fetchAllStudentWorksheets } from './services/worksheetServices'
import { useParams } from "react-router-dom"

export const StudentAnswers = ({ user }) => {

    const [studentAnswers, setStudentAnswer] = useState({})
    const [studentWorksheets, setStudentWorksheets] = useState([])

    const { worksheetId } = useParams()

    const handlestudentWorksheetFetch = () => {
        fetchAllStudentWorksheets().then((data) => {
            // this needs to be fixed
            const filteredData = data.filter((datum) => datum.studentId === user.id && datum?.id === worksheetId * 1)
            setStudentWorksheets(filteredData)
        })
    }

    useEffect(() => {
        handlestudentWorksheetFetch()
    }, [user?.id])

    return (
        <>
            {studentWorksheets.map((studentWorksheet, index) => (
                <ul key={index}>
                    {studentWorksheet?.worksheet?.questionAndAnswers.map((sheet, sheetIndex) => (
                        <li key={sheetIndex}>
                            <p>{sheet?.question}</p>
                            <input placeholder="answer" />
                            <p>{sheet?.hint}</p>
                        </li>
                    ))}
                </ul>
            ))}
        </>
    )
}
