import { useEffect, useState } from "react"
import { fetchGrades } from "../../services/gradeServices"
import { fetchSubjects } from "../../services/subjectServices"

export const CreateWorksheet = ({ user }) => {

    const [grades, setGrades] = useState([])
    const [subjects, setSubjects] = useState([])
    const [worksheetForm, setWorksheetForm] = useState({})

    const handleWorksheetFetchCalls = () => {
        fetchGrades().then((data) => {
            setGrades(data)
        })
        fetchSubjects().then((data) => {
            setSubjects(data)
        })
    }

    useEffect(() => {
        handleWorksheetFetchCalls()
    }, [])

    return (
        <>

            <input
                placeholder="insert title"
                type="text" />
            <select>
                <option>choose a grade</option>
                {grades.map((grade) => <option key={grade?.id} value={grade?.id}>{grade?.grade}</option>)}
            </select>
            <select
                className="inline-flex items-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                name='subjectId'
                onChange={"handleUserChoices"}>
                <option>choose a subject</option>
                {subjects.map((subject) => <option key={subject?.id} value={subject?.id}>{subject?.name}</option>)}
            </select>
            <button>
                Publish
            </button>
            <label>
                Question
            </label>
            <input
                type="text" />
            <label>
                Answer
            </label>
            <input
                type="text" />
            <label>
                Hint?
            </label>
            <input
                type="text" />
        </>
    )
}
