import { useEffect, useState } from "react"
import { fetchAllStudentWorksheets } from './services/worksheetServices'
import { useParams } from "react-router-dom"

export const StudentAnswers = ({ user }) => {

    // const [studentAnswers, setStudentAnswer] = useState({})
    const [studentWorksheet, setStudentWorksheet] = useState([])

    const { worksheetId } = useParams()

    const handleAnswerInput = (event, sheet) => {
