    useEffect(() => {
        handlestudentWorksheetFetch()
    }, [user?.id])

    return (
        <>
            <ul>
                {studentWorksheet?.worksheet?.questionAndAnswers.map((sheet, sheetIndex) => (
                    <li key={sheetIndex}>
                        <p>{sheet?.question}</p>
                        <input onChange={(event) => { handleAnswerInput(event, sheet) }} placeholder="answer" />
                        <p>{sheet?.hint}</p>
                    </li>
                ))}
            </ul>
        </>
    )
}
