import { useState } from "react"
import { addHeaderField, addInputField } from "./worksheetInputs"

export const TestModule2 = ({ user }) => {

    const [chosenInputs, setChosenInputs] = useState([])
    const [userQuestions, setUserQuestions] = useState([])

    const handleAddInputButton = (event) => {
        event.preventDefault()
        const copy = [...chosenInputs]
        copy.push(addInputField(userQuestions, setUserQuestions, chosenInputs))
        setChosenInputs(copy)
    }

    // teacher view should be able to view ALL students she creater
    // parent view should be able to view only the student she syncs with the student code

    return (
        <>
            <div class="isolate bg-white px-6 lg:px-8">

                <form action="#" method="POST" class="mx-auto mt-16 max-w-xl sm:mt-20">
                    {addHeaderField()}
                    <section
                        className="flex justify-between">
                        <button
                            onClick={handleAddInputButton}>
                            add input
                        </button>
                    </section>
                </form>
            </div>
        </>
    )
}
