export const fetchStudents = () => {
    return (
        fetch("http://localhost:8088/students").then((response) => response.json())
    )
}

export const fetchUserStudents = (id) => {
    return (
        fetch(`http://localhost:8088/students?userId=${id}`).then((response) => response.json())
    )
}

export const fetchStudentById = (id) => {
    return (
        fetch(`http://localhost:8088/students/${id}`).then((response) => response.json())
    )
}

export const postStudentToDatabase = () => {
    return (
        fetch('http://localhost:8088/students', {})
    )
}

export const createUser = (customer) => {
    console.log(customer)
    return fetch("http://localhost:8088/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(customer),
    }).then((res) => res.json())
  }
