import { useEffect, useState } from "react"
import { fetchGrades } from '../services/gradeServices'
import { fetchUserStudents } from '../services/studentServices'

export const Students = ({ user }) => {

    const [chosenStudent, setChosenStudent] = useState({})
    const [grades, setGrades] = useState([])
    const [userStudents, setUserStudents] = useState([])

    const handleChosenStudent = (studentId) => {
        const updatedChosenStudent = userStudents.find((student) => student.id === studentId)
        setChosenStudent(updatedChosenStudent)
    }

    const handleFetchGrades = () => {
        fetchGrades().then((data) => { setGrades(data) })
    }

    const handleFetchUserStudents = () => {
        fetchUserStudents(user.id).then((data) => {
            setUserStudents(data)
        })
    }

    useEffect(() => {
        handleFetchUserStudents()
        handleFetchGrades()
    }, [user.id])

    return (
        <>
            <div className="flex">
                <aside className="border w-3/12">
                    <div className="flex">
                        <section className="flex flex-col w-full">
                            <p className="flex pb-2 pl-5 pt-5">{userStudents.length} students</p>
                            {userStudents.map((userStudent) => {
                                return (
                                    <>
                                        <div className="flex gap-5 justify-start hover:bg-gray-200 ml-1 mr-1 pb-3 pl-5 pt-3 rounded-md w-6/6">
                                            <div className="">
                                                <img className="bg-white hover:w-24 rounded-full shadow-lg w-20" src={`https://api.dicebear.com/7.x/big-smile/svg?seed=${userStudent.firstName}%20${userStudent.lastName}`} alt="" onClick={() => { handleChosenStudent(userStudent.id) }} />
                                            </div>
                                            <div className="flex flex-col justify-center gap-2">
                                                <p>{userStudent.firstName} {userStudent.lastName}</p>
                                                <p className="text-gray-500">{grades.find((grade) => grade.id === userStudent.gradeId)?.level} Grade</p>
                                            </div>
                                        </div>
                                    </>
                                )
                            })}
                        </section>
                    </div>
                </aside>
                <main className="bg-gray-500 w-full">
                    <p>{chosenStudent.firstName} {chosenStudent.lastName}</p>
                    <p>student code: {chosenStudent.studentCode}{chosenStudent.id}</p>
                    <div className="bg-white border pb-3 pl- pt-3 rounded-md">
                        <p>{chosenStudent?.email?.length > 0 ? `Email: ${chosenStudent.email}` : "select a student to see the student email"}</p>
                    </div>
                </main>
            </div>
        </>
    )
}
