import { useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { createUser, getUserByEmail } from "../services/userServices"
import { fetchStudents } from '../services/studentServices'

export const Register = (props) => {

  const [studentCode, setStudentCode] = useState(0)
  const [user, setUser] = useState({})

  const navigate = useNavigate()

  const registerNewUser = (copyOfUser) => {
    createUser(copyOfUser).then((createdUser) => {
      if (createdUser.hasOwnProperty("id")) {
        localStorage.setItem(
          "capstone_user",
          JSON.stringify({
            id: createdUser.id,
          }),
        )
        navigate("/")
      }
    })
  }
  // i may need this for the student code
  const handleStudentCode = (event) => {
    setStudentCode(event.target.value)
  }
  // this may need to be changed arounda bit
  const handleRegisterUser = (e) => {
    e.preventDefault()
    const copyOfUser = { ...user }
    copyOfUser.userTypeId = parseInt(copyOfUser?.userTypeId)
    // setUser(copyOfUser)
    getUserByEmail(user?.email).then((response) => {
      if (response.length > 0) {
        // Duplicate email. No good.
        window.alert("Account with that email address already exists")
      } else {
        // Good email, create user.
        registerNewUser(copyOfUser)
      }
    })
  }
  // i may need this for the user input AND make this shorter syntax
  const handleUserInput = (event) => {
    const copy = { ...user }
    const name = event?.target?.name
    const value = event?.target?.value
    copy[name] = value
    setUser(copy)
  }

  useEffect(() => { }, [])

  return (
    <>
      <div className="h-screen flex items-center justify-center">
        <form className="border rounded-md flex flex-col items-center p-10 shadow-lg">
          <h1 className="font-bold text-4xl">Register</h1>
          <label className="pt-5 pl-5 self-start">First Name</label>
          <input
            className="border h-10 hover:border-blue-500/100 w-80 rounded-md text-center"
            placeholder="Enter your first name"
            required
            type="text" />
          <p className="pt-5 pl-5 self-start">Last Name</p>
          <input
            className="border h-10 hover:border-blue-500/100 w-80 rounded-md text-center"
            placeholder="Enter your last name"
            required
            type="text" />
          <p className="pt-5 pl-5 self-start">Email</p>
          <input
            className="border h-10 hover:border-blue-500/100 w-80 rounded-md text-center"
            placeholder="Enter your email"
            required
            type="text" />
          <section className="flex flex-col gap-5 items-start pt-5 w-80">
            <div>
              <input id="parent" type="checkbox" /><label htmlFor="parent">label</label>
            </div>
            <div>
              <input type="checkbox" /><span className="pl-2">Student?</span>
            </div>
          </section>
          <div className="p-5">
            <button className="bg-blue-500 h-10 hover:bg-blue-400 rounded-md w-80">
              Sign up
            </button>
          </div>
          <p>Already a member?</p>
          <Link className="pt-3" to="/login">
            <button className="bg-green-500 h-10 hover:bg-green-400 rounded-md w-80">
              Sign In
            </button>
          </Link>
        </form>
      </div>
    </>
  )
}
