import { useEffect, useState } from "react"
import { fetchAllStudentWorksheets, putStudentWorksheetInDatabase } from "../services/worksheetServices"
import { useNavigate } from "react-router-dom"

export const ReviewWorksheets = () => {

    const [studentWorksheets, setStudentWorksheets] = useState([])

    const navigate = useNavigate()

    const fetchCalls = () => {
        fetchAllStudentWorksheets().then((data) => {
            const filterStudentWorksheets = data.filter((datum) => datum.isApproved !== true && datum.isComplete === true)
            setStudentWorksheets(filterStudentWorksheets)
        })
    }

    const approveWorksheet = (event, studentWorksheet) => {
        event.preventDefault()
        const copy = { ...studentWorksheet }
        copy.isApproved = true
        delete copy.student
        delete copy.worksheet
        putStudentWorksheetInDatabase(copy).then(() => {
            navigate('/')
        })
    }

    const rejectWorksheet = (event, studentWorksheet) => {
        event.preventDefault()
        const copy = { ...studentWorksheet }
        copy.isApproved = false
        delete copy.student
        delete copy.worksheet
        putStudentWorksheetInDatabase(copy).then(() => {
            navigate('/')
        })
    }

    useEffect(() => {
        fetchCalls()
    }, [])

    return (
        <>
            <div className="relative h-screen isolate overflow-hidden bg-gray-900 ">
                <div className="mx-auto max-w-7xl px-6 lg:px-8 "></div>
                <div className="container mx-auto p-4">
                    <h2 className="text-2xl text-white font-bold mb-4">Student Worksheet</h2>
                    <form onSubmit={"handleUpdateButton"} className="max-w-lg mx-auto">
                        {studentWorksheets?.map((item, index) => {
                            if (item.isApproved) {
                                return (
                                    null
                                )
                            } else {

                                {
                                    item?.questionAndAnswers?.map((studentAnswer) => {
                                        return (
                                            <>
                                                <div key={index} className="mb-4">
                                                    <label className="block text-sm font-medium text-gray-600">Question {index + 1}</label>
                                                    <input
                                                        type="text"
                                                        value={item?.question}
                                                        className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                                                        required
                                                    />
                                                    <label className="block text-sm font-medium text-gray-600">Answer</label>
                                                    <input
                                                        id={item?.id}
                                                        type="text"
                                                        value={item?.studentAnswer}
                                                        onChange={"(event) => { handleAnswerInput(event) }"}
                                                        className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                                                        required
                                                    />
                                                    <label className="block text-sm font-medium text-gray-600">Hints</label>
                                                    <textarea
                                                        value={item?.hint}
                                                        rows="5"
                                                        className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                                                    ></textarea>
                                                </div>
                                            </>
                                        )
                                    })
                                }
                            }
                        })}
                        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                            Submit
                        </button>
                    </form>
                </div>
                <div className="absolute left-1/2 top-0 -z-10 -translate-x-1/2 blur-3xl xl:-top-6" aria-hidden="true">
                    <div className="aspect-[1155/678] w-[72.1875rem] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30" ></div>
                </div>
            </div>
            {/* <ul
                className="bg-blue-500">
                {studentWorksheets.map((studentWorksheet) => {

                    if (studentWorksheet.isApproved) {
                        return (
                            null
                        )
                    } else {
                        return (
                            <li>
                                <p>Title - {studentWorksheet?.worksheet?.title}</p>
                                {studentWorksheet?.questionAndAnswers?.map((studentAnswer) => {
                                    return (
                                        <>
                                            <p>Question - {studentAnswer?.question}</p>
                                            <p>Student's Answer - {studentAnswer?.studentAnswer}</p>
                                        </>
                                    )
                                })}
                                <button onClick={(event) => { approveWorksheet(event, studentWorksheet) }}>Approve</button>
                                <button onClick={(event) => { rejectWorksheet(event, studentWorksheet) }}>Reject</button>
                            </li>
                        )
                    }
                })}

            </ul> */}
        </>
    )
}
