import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { fetchAllStudentWorksheets, putStudentWorksheetInDatabase } from "../services/worksheetServices"

export const StudentAnswers = ({ user }) => {

    const [studentWorksheet, setStudentWorksheet] = useState([])

    const navigate = useNavigate()

    const { worksheetId } = useParams()

    const handleAnswerInput = (event) => {
        const copy = { ...studentWorksheet }
        const id = parseInt(event.target.id)
        // const name = event.target.name
        const value = event.target.value
        const updateQuestionAndAnswers = copy?.worksheet?.questionAndAnswers?.map((question) => {
            if (question.id === id) {
                return (
                    { ...question, studentAnswer: value }
                )
            } else {
                return (
                    question
                )
            }
        })
        copy.worksheet.questionAndAnswers = updateQuestionAndAnswers
        setStudentWorksheet(copy)
    }

    const handlestudentWorksheetFetch = () => {
        fetchAllStudentWorksheets().then((data) => {
            const filteredData = data.filter((datum) => datum.studentId === user.studentId && datum?.id === worksheetId * 1)
            setStudentWorksheet(filteredData[0])
        })
    }

    const handleUpdateButton = () => {
        const copy = { ...studentWorksheet }
        const questionAndAnswers = copy?.worksheet?.questionAndAnswers
        delete copy.student
        delete copy.worksheet
        copy.questionAndAnswers = questionAndAnswers
        console.log("ðŸš€ ~ file: StudentAnswers.js:45 ~ handleUpdateButton ~ copy:", copy)
        putStudentWorksheetInDatabase(copy).then(() => {
            navigate('/worksheets')
        })
    }

    useEffect(() => {
        handlestudentWorksheetFetch()
    }, [user?.id])

    return (
        <>
            {/* <ul>
                {studentWorksheet?.worksheet?.questionAndAnswers?.map((sheet, sheetIndex) => (
                    <li key={sheetIndex}>
                        <p>{sheet?.question}</p>
                        <input className="" id={sheet.id} onChange={(event) => { handleAnswerInput(event) }} placeholder="type your answer here" />
                        <p>{sheet?.hint}</p>
                    </li>
                ))}
                <button className="bg-blue-500" onClick={handleUpdateButton}>update button</button>
            </ul> */}
            <div className="container mx-auto p-4">
            <h2 className="text-2xl font-bold mb-4">Create Student Worksheet</h2>
            <form onSubmit={"handleSubmit"} className="max-w-lg mx-auto">
                {studentWorksheet?.worksheet?.questionAndAnswers?.map((item, index) => (
                    <div key={index} className="mb-4">
                        <label className="block text-sm font-medium text-gray-600">Question {index + 1}</label>
                        <input
                            type="text"
                            value={item.question}
                            onChange={"(e) => handleQuestionChange(e, index)"}
                            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                            required
                        />
                        <label className="block text-sm font-medium text-gray-600">Answer</label>
                        <input
                            type="text"
                            value={item.answer}
                            onChange={"(e) => handleAnswerChange(e, index)"}
                            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                            required
                        />
                        <label className="block text-sm font-medium text-gray-600">Hints</label>
                        <textarea
                            value={item.hints}
                            onChange={"(e) => handleHintsChange(e, index)"}
                            rows="5"
                            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                        ></textarea>
                    </div>
                ))}
                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                    Submit
                </button>
            </form>
        </div>
        </>
    )
}
