import { useEffect } from "react"
import { useState } from "react"
import { Link } from "react-router-dom"
import { fetchUserWorksheets } from "../services/worksheetServices"
import { fetchSubjects } from "../services/subjectServices"
import { fetchUserStudents } from "../services/studentServices"

export const Worksheets = ({ user }) => {

  const [students, setStudents] = useState([])
  const [subjects, setSubjects] = useState([])
  const [worksheets, setWorksheets] = useState([])
  const [worksheetsToDisplay, setworksheetsToDisplay] = useState([])

  const handleFilters = (event) => {
    const name = event.target.name
    const value = event.target.value
    const filterWorksheets = worksheets.filter((worksheet) => {
      return worksheet[name] === value
    })
    setworksheetsToDisplay(filterWorksheets)
  }

  const handleFetchCalls = () => {
    fetchUserWorksheets(user.id).then((data) => {
      setWorksheets(data)
    })
    fetchSubjects().then((data) => {
      setSubjects(data)
    })
    fetchUserStudents(user.id).then((data) => {
      setStudents(data)
    })
  }

  useEffect(() => {
    handleFetchCalls()
  }, [user])

  useEffect(() => {
    setworksheetsToDisplay(worksheets)
  }, [worksheets])

  // add a like relationship to the posts

  // teacher can specify what subject she teaches

  // a teacher should be able to upload a worksheet and assign it to a student
  // a parent can then re upload the worksheet to show that it is ready for review
  // a teacher should be able to then very that it is completed and mark it as completed

  // a post or comment page for people to communicate and leave comments on the post 

  return (
    <>
      <div style="width: 100%; background-color: white;">
        <div style="position: relative; padding-bottom: 149%; padding-top: 0; height: 0; border: solid 1px #ddd;">
          <iframe frameborder="0" width="1000" height="1415" style="position: absolute; top: 0; left: 0; width: 100%; height: 100%;" src="https://www.topworksheets.com/embed/multiplication-of-3-digit-65342a474d91a" type="text/html" allowscriptaccess="always" allowfullscreen="true" scrolling="yes" allownetworking="all" allow="camera;microphone">
            </iframe>
            </div>
            <div style="width: 100%; border: solid 1px #ddd; border-top: 0; display: flex; align-items: center; justify-content: flex-end;">
              <a href="https://www.topworksheets.com/en/math/multiplication/multiplication-of-3-digit-65342a474d91a" title="Multiplication of 3 digit">
                Multiplication of 3 digit
                </a>
                <a href="https://www.topworksheets.com/en/math/multiplication/multiplication-of-3-digit-65342a474d91a" title="Powered by TopWorksheets">
                  <img style="margin: 10px;" src="https://www.topworksheets.com/assets/images/logo.png" width="80px" style="border: 0;" /><
                    /a></div></div>
    </>
  )
}
