import { useState } from "react"

export const TestModule = ({ handleToggle }) => {

    const [action, setAction] = useState("no action")
    const [steps, setSteps] = useState(0)
    const [type, setType] = useState("")

    const pikmin = {
        arm: 2,
        eyes: 2,
    }

    const pikminNose = [
        {
            id: 1,
            nose: true,
            type: "pointy"
        },
        {
            id: 2,
            nose: false,
            type: ""
        }
    ]

    const pikminLegs = [
        {
            id: 1,
            legs: true,
            howMany: 2
        },
        {
            id: 2,
            legs: false,
            howMany: 0
        }
    ]

    const pikminLeaf = [
        {
            id: 1,
            hasLeaf: true,
        },
        {
            id: 2,
            hasLeaf: false,
        }
    ]

    const pikminFlower = [
        {
            id: 1,
            hasFlower: true
        },
        {
            id: 2,
            hasFlower: false
        }
    ]

    const pikminTypes = [
        {
            id: 1,
            type: "red"
        },
        {
            id: 2,
            type: "yellow"
        },
        {
            id: 3,
            type: "blue"
        },
        {
            id: 4,
            type: "rock"
        },
        {
            id: 2,
            type: "winged"
        },
        {
            id: 2,
            type: "white"
        },
        {
            id: 2,
            type: "purple"
        },
        {
            id: 2,
            type: "ice"
        },
        {
            id: 2,
            type: "glow"
        }
    ]

    const handleChange = (event) => {
        setType(event.target.value)
    }

    const handleWalk = (event) => {
        const walk = type + " pikmin walked " + steps + " steps"
    }

    return (
        <>
            <article
                className="grid min-h-full place-items-center bg-white px-6 py-24 sm:py-32 lg:px-8 text-center">
                <form
                    onSubmit={""}>
                    <h1
                        className="mt-4 text-3xl font-bold tracking-tight text-gray-900 sm:text-5xl">
                        Pikmin
                    </h1>
                    {/* <label
                        className="text-base leading-7 text-gray-600">
                        Login to begin
                        <input
                            className="mt-3 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6 text-center"
                            onChange={"(evt) => set(evt.target.value)"}
                            placeholder="Please enter your email"
                            required />
                    </label> */}
                    <label
                        className="text-base leading-7 text-gray-600">
                        {action}
                        <select
                            className="mt-3 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6 text-center"
                            onChange={handleChange}
                            required >
                                <option>please choose a type</option>
                                {pikminTypes.map((type) => {
                                    return (
                                        <option key={type.id}>{type.type}</option>
                                    )
                                })}
                            </select>
                    </label>
                    <div
                        className="mt-10 flex items-center justify-center gap-x-6">
                        <button
                            href="#"
                            className="rounded-md bg-indigo-600 px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                            onClick={handleWalk}>
                            Walk
                        </button>
                        {/* <Link
            className="text-sm font-semibold text-gray-900"
            to="/register">
            Sign up
            <span>
              &rarr;
            </span>
          </Link> */}
                    </div>
                </form>
            </article >
        </>
    )
}
