}

const handleUserInputs = (event) => {
    const updatedUserStudent = { ...userStudent, [event.target.name]: event.target.value }
    setUserStudent(updatedUserStudent)
}

useEffect(() => {
    handleFetchGrades()
}, [])

return (
    <>
        <input className="border" name="firstName" onChange={handleUserInputs} placeholder="Enter first name..." />
        <input className="border" name="lastName" onChange={handleUserInputs} placeholder="Enter last name..." />
        <select name="gradeId" onChange={handleGradeSelectInput}>
            <option>choose a grade</option>
            {grades.map((grade, index) => <option key={index} value={grade.id}>{grade.level}</option>)}
        </select>
        <button onClick={handleCreateStudent}>Create</button>
    </>
)
}
