import { useEffect, useState } from "react"
import { deleteWorksheetFromDatabase, fetchAllStudentWorksheets, fetchUserWorksheets } from "../services/worksheetServices"
import { useNavigate } from "react-router-dom"
import { NavBar } from "../components/NavBar"

export const Worksheets = ({ user }) => {

    const [studentWorksheets, setStudentWorksheets] = useState([])
    const [worksheets, setWorksheets] = useState([])

    const navigate = useNavigate()

    const handleDeleteButton = (event, id) => {
        event.preventDefault()
        // deleteWorksheetFromDatabase(id).then(() => {
        //     handleWorksheetsFetchCall()
        // })
    }

    const handleWorksheetsFetchCall = () => {
        fetchUserWorksheets(user.id).then((data) => {
            setWorksheets(data)
        })
        fetchAllStudentWorksheets().then((data) => {
            setStudentWorksheets(data)
        })
    }
    useEffect(() => {
        handleWorksheetsFetchCall()
    }, [user.id])

    return (
        <div className="flex bg-gray-500">
            <aside className="bg-">side bar will go here</aside>
            <div>
                <header>
                    <h1 className="text-4xl font-medium">Worksheets</h1>
                </header>
                <main className="grid grid-cols-4">
                    {worksheets.map((worksheet) => {
                        return (
                            <section className="m-3">
                                <img src="https://placehold.co/200" alt="" />
                                <p>{worksheet?.title}</p>
                                {/* <p>{worksheet?.grade?.grade} Grade</p> */}
                                {/* <p>{worksheet?.subject?.name}</p> */}
                                {/* <p>{worksheet?.questionAndAnswers?.length} Questions</p> */}
                                {worksheet?.userId === user?.id &&
                                    <div className="flex justify-around">
                                        <button className="rounded-full bg-indigo-500 w-auto" onClick={() => { navigate(`edit/${worksheet?.id}`) }}>
                                            Edit
                                        </button>
                                        <button className="rounded-full bg-indigo-500 w-auto" onClick={(event) => {
                                            handleDeleteButton(event, worksheet?.id)
                                        }}>
                                            Delete
                                        </button>
                                        <button className="rounded-full bg-indigo-500 w-auto">
                                            Like
                                        </button>
                                    </div>
                                }
                            </section>
                        )
                    })}
                </main>
            </div>
            <aside>side bar will go here</aside>
        </div>
    )
}
